class NAME_1 {
public:
    virtual ~NAME_1() {}

    virtual void render() const = 0;
    virtual void move(float dX, float dY) = 0;
    virtual void rotate(float deg, float pointX = 0.0f, float pointY = 0.0f) = 0;
    virtual void scale(float scaleFactor, float pointX = 0.0f, float pointY = 0.0f) = 0;
};

class Triangle : public NAME_1 {
public:
    Triangle(float x, float y, float base, float height, const Color& shapeColor)
        : NAME_1(), posX(x), posY(y), baseLength(base), heightLength(height), color(shapeColor) {}

    void render() const override {
        glColor3f(color.red, color.green, color.blue);
        glBegin(GL_TRIANGLES);
        glVertex2f(posX, posY);
        glVertex2f(posX + baseLength, posY);
        glVertex2f(posX + baseLength / 2, posY + heightLength);
        glEnd();
    }

    void move(float dX, float dY) override {
        posX += dX;
        posY += dY;
    }

    void rotate(float deg, float pointX = 0.0f, float pointY = 0.0f) override {
        float radians = deg * (M_PI / 180.0f);
        float newX = pointX + (posX - pointX) * cos(radians) - (posY - pointY) * sin(radians);
        float newY = pointY + (posX - pointX) * sin(radians) + (posY - pointY) * cos(radians);
        posX = newX;
        posY = newY;
    }

    void scale(float scaleFactor, float pointX = 0.0f, float pointY = 0.0f) override {
        posX = pointX + scaleFactor * (posX - pointX);
        posY = pointY + scaleFactor * (posY - pointY);
        baseLength *= scaleFactor;
        heightLength *= scaleFactor;
    }

private:
    float posX;
    float posY;
    float baseLength;
    float heightLength;
    Color color;
};

class Rectangle : public NAME_1 {
