void dfs(int node,vector<int>adj[],vector<bool>&vis,stack<int> &st){
    vis[node] = true;
    for(auto nbr:adj[node]){
        if (!vis[nbr])
        {
            dfs(nbr,adj,vis,st);
        }
    }
    st.push(node);
}

void reverse_dfs(int node,vector<int>transpose[],vector<bool>&vis,stack<int>&st,vector<int>&curr_scc){
    vis[node] = true;
    curr_scc.push_back(node);
    for(auto nbr:transpose[node]){
        if (!vis[nbr])
        {
            reverse_dfs(nbr,transpose,vis,st,curr_scc);
        }
    }
}

vector<vector<int>> stronglyConnectedComponents(int n, vector<vector<int>> &edges){
    vector<int>adj[n];
    //store the input
    for (int i = 0; i < edges.size(); ++i)
    {
        int u = edges[i][0];
        int v = edges[i][1];
        adj[u].push_back(v);
    }
    vector<bool>vis(n,false);
    stack<int>st;
    //sort the graph in order of increasing wait time (TOPO sort)
    for (int i = 0; i < n; ++i)
    {
        if (!vis[i])
        {
            dfs(i,adj,vis,st);
        }
    }
    //reversing the edges and changing the visited array to false
    vector<int>transpose[n];
    for (int i = 0; i < n; ++i)
    {
        vis[i] = false;
        for(auto nbr:adj[i]){
            transpose[nbr].push_back(i);
        }
    }
    //traversing on the reversed graph using the topologicla order obtained above
    vector<vector<int>> ans;
    while(!st.empty()){
        int node = st.top();
        st.pop();
        if (!vis[node])
        {
            vector<int>curr_scc;
            reverse_dfs(node,transpose,vis,st,curr_scc);
            ans.push_back(curr_scc);
        }
    }
    return ans;
}

