  $replacement = '<h$1$2 id="$3">$3</h$1>';

  return preg_replace_callback($pattern, function ($matches) {
      $anchor_id = sanitize_title_with_dashes($matches[3]);
      return '<h' . $matches[1] . $matches[2] . ' id="' . $anchor_id . '">' . $matches[3] . '</h' . $matches[1] . '>';
  }, $content);
