```rust
use std::time::{Duration, SystemTime};

fn is_first_timestamp_a_week_older(timestamp1: SystemTime, timestamp2: SystemTime) -> bool {
    match timestamp2.duration_since(timestamp1) {
        Ok(duration) => duration >= Duration::from_secs(7 * 24 * 60 * 60),
        Err(_) => false,
    }
}

fn main() {
    let timestamp1 = SystemTime::now();
    let timestamp2 = timestamp1
        .checked_add(Duration::from_secs(8 * 24 * 60 * 60))
