```dart
class SwipePages extends StatefulWidget {
  final int attachmentsNumber;

  SwipePages({required this.attachmentsNumber});

  @override
  _SwipePagesState createState() => _SwipePagesState();
}

class _SwipePagesState extends State<SwipePages> {
  late PageController _pageController;

  @override
  void initState() {
    super.initState();
    _pageController = PageController(initialPage: 0);
  }

  @override
  void dispose() {
    _pageController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return PageView(
      controller: _pageController,
      children: List.generate(
        widget.attachmentsNumber,
            (index) => Container(
          child: Center(
            child: Text('Page $index'),
          ),
        ),
      ),
    );
  }
}
```

